import axios from 'axios'

export default class AppService {

    constructor() {

        this.service = axios.create({
            baseURL: 'http://localhost:5000/api',
            withCredentials: true
        })
    }

    getAllItems = () => this.service.get('/all')
    newItem = item => this.service.post('/new', item)
    getOneItem = id => this.service.get(`/${id}`)
    editItem = id => this.service.post(`/${id}/edit`)
    deleteItem = id => this.service.get(`${id}/delete`)


}
