import React, { Component } from 'react'
import PropTypes from 'prop-types'

class Form extends Component {
    handleSubmit = e => {
        e.preventDefault()
        const { editMode, post } = this.props
        const title = this.titleRef.value
        const body = this.bodyRef.value
        if (editMode) {
            const data = {
                title,
                body
            }
            this.props.dispatch({ type: 'UPDATE', id: post.id, data })
        }
        else {
            const data = {
                id: new Date(),
                name: "",
                description: "",
                category: "",
                editing: false
            }
            this.props.dispatch({ type: 'ADD_POST', data })
        }
    }

    render() {
        const { editMode, post } = this.props
        const pageTitle = editMode ? 'Edit Post' : 'Create Post'
        const buttonTitle = editMode ? 'Update' : 'Post'
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input
                        required
                        type="text"
                        ref={input => this.titleRef = input}
                        placeholder="Name"
                        defaultValue={post.name}
                    />
                    <textarea
                        required
                        rows="5"
                        ref={input => this.bodyRef = input}
                        cols="28"
                        placeholder="Enter Post"
                        defaultValue={post.body}
                    />

                    <h3 style={{ color: 'lightseagreen' }}>{pageTitle}</h3>
                    <hr></hr>
                    <Form className="newForm" onSubmit={this.handleSubmit}>
                        <Form.Group>
                            <Form.Label style={{ color: 'SlateBlue' }}>Name</Form.Label>
                            <Form.Control onChange={this.handleInputChange} value={this.state.name} name="name" type="text" />
                            <Form.Text className="text-muted">The devil is in the details ;)</Form.Text>
                        </Form.Group>

                        <Form.Group>
                            <Form.Label style={{ color: 'SlateBlue' }}>Description</Form.Label>
                            <Form.Control onChange={this.handleInputChange} value={this.state.description} name="description" type="text" />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label style={{ color: 'SlateBlue' }}>Category</Form.Label>
                            <Form.Control onChange={this.handleInputChange} value={this.state.category} name="category" type="text" />
                        </Form.Group>

                        {/* <Form.Group>
                        <Form.Label>Location</Form.Label>
                        <Form.Control onChange={this.handleInputChange} value={this.state.location} name="location" type="number" />
                    </Form.Group> */}

                        {/* <Form.Group>
                        <Form.Label style={{ color: 'SlateBlue' }}>Image (URL)</Form.Label>
                        <Form.Control onChange={this.handleInputChange} value={this.state.imageUrl} name="imageUrl" type="text" />
                    </Form.Group> */}

                        {/* <Form.Group>
                        <Form.Label style={{ color: 'SlateBlue' }}>Found by</Form.Label>
                        <Form.Control onChange={this.handleInputChange} value={this.state.foundBy} name="foundBy" type="text" />
                    </Form.Group> */}


                        <Button className="btn btn-light btn-block btn-sm details auth" type="submit">Submit</Button>
                    </Form>
                    <button>{buttonTitle}</button>
                </form>
            </div>
        );
    }
}

Form.propTypes = {
    editMode: PropTypes.bool,
    post: PropTypes.object
}

Form.defaultProps = {
    editMode: false,    // false: Create mode, true: Edit mode
    post: {
        name: "",
        description: "",
        category: ""
    }    // Pass defined Post object in create mode in order not to get undefined objects in 'defaultValue's of inputs.
}

export default Form
