import React, { Component } from 'react'
import { Link } from 'react-router-dom'

import UserService from '../../../service/UserService'
import AppService from '../../../service/AppService'
import './profile.css'

import Button from 'react-bootstrap/Button'


class Profile extends Component {
    constructor(props) {
        super(props)
        this.state = {
            user: undefined,
            name: '',
            username: ''
        }
        this.userService = new UserService()
    }

    handleFormSubmit = e => {
        e.preventDefault()
        this.authService
            .editUser(this.props.loggedInUser.id, this.state.user)
            .then(response => {
                this.props.setTheUser(response.data)
                this.props.handleToast(true, 'Signed up successfully')
                this.props.history.push('/all')
            })
            .catch(err => console.log(err.response.data.message))   // Error handling yay!
    }
    editUser = (id) => {
        this.userService.editUser(id)
            .then(response => this.setState({ user: response.data }))
            .catch(err => console.log(err))
    }

    render() {
        console.log(this.props)
        return (
            <>
                <div className="container">
                    <h2> Hi, {this.props.loggedInUser.name}!</h2>
                    <p><img className="avatarUser" src={this.props.loggedInUser.avatar} alt="user avatar" /></p>
                    <Button className="btn btn-light btn-block btn-sm details auth" onClick={() => this.editUser(this.props.loggedInUser._id)}>Edit</Button>
                    <hr></hr>
                    <br></br>
                    <h4>These are your listed items:</h4>
                    <br></br>

                </div>

            </>
        )
    }
}

export default Profile
